void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    final Books b;
    return GetMaterialApp(
      title: 'task three',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      home: HomePage(),
    );
  }
}

class Books {
  final RxString bookName, authorName, price, image, description;
  final RxBool isSold;
  Books(String bookNameb, String authorNameb, String priceb, String imageb,
      String descriptionb,
      [bool? isSold])
      : bookName = bookNameb.obs,
        authorName = authorNameb.obs,
        price = priceb.obs,
        image = imageb.obs,
        description = descriptionb.obs,
        isSold = (isSold ?? false).obs;

  static makeAsTrue(RxBool x) {
    x.value = true;
  }

  static RxList<Books> MyBooks = <Books>[
    Books(
        "One Hundred Years of Solitude",
        "Gabriel García Márquez",
        "2.99",
        "data:image/jpeg;base64,",
        "One Hundred Years of Solitude is the first piece of literature since the Book of Genesis that should be required reading for the entire human race. . . . Mr. Garcia Marquez has done nothing less than to create in the reader a sense of all that is profound, meaningful, and meaningless in life."),
    Books(
        "1984",
        "George Orwell's",
        "10.99",
        "data:image/jpeg;base64,",
        "The reader of George Orwell's novel 1984 is still shocked by its frightening perception of life in a totalitarian environment, as he begins to realize the congruence of what the author describes with the current reality. Intellectual” that combines two opposites, and the “reduced talk” in which the regime deprives its opponents of tools for expressing rejection, the “thought police” and the “Ministry of Love” that specialize in pain and despair and the extermination of dissidents, as well as the “Ministry of Peace” that wages war, and mechanisms Writing that appeals to the masses with pornography, and so on.")
  ].obs;

  static add(String bookName, String authorName, String price, String image,
      String description) {
    MyBooks.add(Books(bookName, authorName, price, image, description));
  }
}


